<?php

namespace LokiTuoResultBundle\Repository;

use LokiTuoResultBundle\Entity\Message;
use LokiTuoResultBundle\Entity\Player;
use LokiUserBundle\Entity\User;

/**
 * MessageRepository
 *
 * This class was generated by the PhpStorm "Php Annotations" Plugin. Add your own custom
 * repository methods below.
 */
class MessageRepository extends AbstractBaseRepository
{

    /**
     * Returns the total number of Messages for all Players a User Owns
     * @param User $user
     * @return int
     */
    public function countForUser(User $user)
    {
        $qb = $this->createQueryBuilder('m')
            ->join('m.player', 'p')
            ->select("COUNT(m.id)")
            ->where("p.owner = :id")
            ->andWhere('m.status = :status')
            ->setParameter("id", $user->getId())
            ->setParameter("status", Message::STATUS_UNREAD);
        $res = $qb->getQuery()->getSingleResult();
        return $res[1];
    }

    public function findUnreadByPlayer(Player $player)
    {
        $qb = $this->createQueryBuilder('m')
            ->join('m.player', 'p')
            ->where("p.id = :id")
            ->andWhere('m.status = :status')
            ->setParameter("id", $player->getId())
            ->setParameter("status", Message::STATUS_UNREAD);
        return $qb->getQuery()->getResult();
    }
}
